import React, { FC } from 'react';
import { PageOpts } from 'nextra';
export { useTheme } from 'next-themes';

interface NextraBlogTheme {
    readMore?: string;
    footer?: React.ReactNode;
    titleSuffix?: string;
    postFooter?: string;
    head?: ({ title, meta }: {
        title: string;
        meta: Record<string, any>;
    }) => React.ReactNode;
    cusdis?: {
        appId: string;
        host?: string;
        lang: string;
    };
    darkMode?: boolean;
    navs?: {
        url: string;
        name: string;
    }[];
    comments?: React.ReactNode;
}
interface BlogPageOpts extends PageOpts {
    meta: Meta;
}
declare type Meta = {
    title?: string;
    type?: 'post' | 'page' | 'posts' | 'tag';
    tag?: string | string[];
    back?: string;
    date?: string;
    description?: string;
    author?: string;
};
interface LayoutProps {
    config: NextraBlogTheme;
    opts: BlogPageOpts;
}

declare const useBlogContext: () => LayoutProps;

declare function Layout(props: any): JSX.Element;
declare function withLayout(route: string, Content: FC, pageOpts: PageOpts, themeConfig: NextraBlogTheme): typeof Layout;

export { BlogPageOpts, LayoutProps, NextraBlogTheme, withLayout as default, useBlogContext };
